mapper.select:
SELECT COUNT(CLUB_CODE)
FROM TENNIS_CLUB
WHERE CLUB_NAME = #{clubName}

SELECT COUNT(CLUB_CODE)
FROM TENNIS_MEMBER
WHERE MEMBER_ID = #{memberId}

mapper.insert:
		INSERT INTO TENNIS_CLUB (
			CLUB_CODE
			, CLUB_NAME
			, CLUB_INTRO
			, CLUB_TIER
			, CLUB_PERSONNEL
			, CLUB_LEADER
		) VALUES (
			#{clubCode}
            , #{clubName}
            , #{clubIntro}
            , 'TIER_001'
            , #{clubPersonnel}
            , #{clubLeader}
		)

mapper.update:
UPDATE TENNIS_MEMBER
SET CLUB_CODE = #{clubCode}
WHERE MEMBER_ID = #{clubLeader}

UPDATE TENNIS_MEMBER
SET CLUB_CODE = #{clubCode}
WHERE MEMBER_ID = #{memberId}

UPDATE TENNIS_CLUB
SET CLUB_HEAD_COUNT = CLUB_HEAD_COUNT + 1
WHERE CLUB_CODE = #{clubCode}

UPDATE TENNIS_MEMBER
SET CLUB_CODE = NULL
WHERE MEMBER_ID = #{memberId}

UPDATE TENNIS_CLUB
SET CLUB_HEAD_COUNT = CLUB_HEAD_COUNT -1
WHERE CLUB_CODE = #{clubCode}

UPDATE TENNIS_MEMBER
SET CLUB_CODE = NULL
WHERE CLUB_CODE = #{clubCode}
